import { func } from 'prop-types'
import {all,fork,call,take,put, takeEvery, takeLatest, delay} from 'redux-saga/effects'

import axios from 'axios'




function logInAPI(data){
    return axios.post('/api/login')
}

function logOutAPI(){
    return axios.post('/api/login')
}



function* logIn(action){

    try{
        yield delay(1000)
        // const result =yield call(logInAPI,action.data) //call은동기니깐 put할때까지 기다려준다
        yield put({
            type:'LOG_IN_SUCCESS',
            // data:result.data
        })

    }catch(err){
        yield put({
            type: 'LOG_IN_FAILURE',
            data:err.response.data,
        })
    }

}



function* logOut(){

    try{
        yield delay(3000)
        // const result =yield call(logOutAPI) //call은동기니깐 put할때까지 기다려준다
        yield put({
            type:'LOG_OUT_SUCCESS',
        })

    }catch(err){
        yield put({
            type: 'LOG_OUT_FAILURE',
            data:err.response.data,
        })
    }

}


function* watchLogin(){
    yield takeLatest('LOG_IN_REQUEST',logIn) //LOG_IN이란 액션이 실행될때까지 기다리겠다
}

function* watchLogOut(){
    yield takeLatest('LOG_OUT_REQUEST',logOut)
}


export default function* userSaga(){
 yield all([
    fork(watchLogin),
    fork(watchLogOut),
 ])
}
