export const initialState={
    isLoggedIn:false,
    isLoggingIn:false,
    isLoggingOut:false,
    me:null,
    signUpData:{},
    loginData:{},
}




export const loginRequestAction=(data)=>{
    return{
        type:'LOG_IN_REQUEST',
        data
    }
}



export const logoutRequestAction=()=>{
    return{
        type:'LOG_OUT_REQUEST',
    }
}

const reducer=(state=initialState,action)=>{
    switch(action.type){
        case 'LOG_IN_REQUEST':
            return {
                    ...state.user,
                    isLoggedIn:false,
                    isLoggingIn:true,

            }
            case 'LOG_IN_SUCCESS':
            return {
                    ...state.user,
                    isLoggingIn:false,
                    isLoggedIn:true,
                    me:{...action.data,nickname:'zerocho'}

            }
            case 'LOG_IN_FAILURE':
            return {
                    ...state.user,
                    isLoggingIn:false,
                    isLoggedIn:false,

            }
            case 'LOG_OUT_REQUEST':
                return {
                        ...state.user,
                        isLoggingOut:true,
                        isLoggedIn:false,

                }
                case 'LOG_OUT_SUCCESS':
                    return {
                            ...state.user,
                            isLoggingOut:false,
                            isLoggedIn:false,
                            me:null

                    }
                    case 'LOG_OUT_FAILURE':
                        return {
                                ...state.user,
                                isLoggingOut:false,

                                isLoggedIn:false,

                        }
        default:
            return state
    }
}

export default reducer
