export const ADD_POST_TO_ME = 'ADD_POST_TO_ME';
export const REMOVE_POST_OF_ME = 'REMOVE_POST_OF_ME';


const dummyUser=(data)=>({
 ...data  ,
 id:1,
 Posts:[{id:1}],
 Followings:[{nickname:'부기초'},{nickname:'영기초'},{nickname:'동로초'}],
 Followers:[{nickname:'부기초'},{nickname:'영기초'},{nickname:'동로초'}],
 nickname:'dong'
})

export const loginRequestAction=(data)=>{
    return{
        type:'LOG_IN_REQUEST',
        data
    }
}



export const logoutRequestAction=()=>{
    return{
        type:'LOG_OUT_REQUEST',
    }
}

const reducer=(state=initialState,action)=>{
    switch(action.type){
    //     case CHANGE_NICKNAME_REQUEST:
    //   draft.changeNicknameLoading = true;
    //   draft.changeNicknameError = null;
    //   draft.changeNicknameDone = false;
    //   break;
    // case CHANGE_NICKNAME_SUCCESS:
    //   draft.changeNicknameLoading = false;
    //   draft.changeNicknameDone = true;
    //   break;
    // case CHANGE_NICKNAME_FAILURE:
    //   draft.changeNicknameLoading = false;
    //   draft.changeNicknameError = action.error;
    //   break;
        case SIGN_UP_REQUEST:
            return {
                    ...state.user,
                    signUpDone:false,
                    signUpLoading:true,

            }
            case SIGN_UP_SUCCESS:
            return {
                    ...state.user,
                    signUpLoading:false,
                    signUpDone:true,
                    // me:dummyUser(action.data)

            }
            case SIGN_UP_FAILURE:
            return {
                signUpLoading:false,
                signUpDone:false,

            }





        case LOG_IN_REQUEST:
            return {
                    ...state.user,
                    logInDone:false,
                    logInLoading:true,

            }
            case LOG_IN_SUCCESS:
            return {
                    ...state.user,
                    logInLoading:false,
                    logInDone:true,
                    me:dummyUser(action.data)

            }
            case LOG_IN_FAILURE:
            return {
                    ...state.user,
                    logInLoading:false,
                    logInDone:false,

            }
            case LOG_OUT_REQUEST:
                return {
                        ...state.user,
                        logOutDone:false,
                        logOutLoading:true,

                }
                case LOG_OUT_SUCCESS:
                    return {
                            ...state.user,
                            logOutLoading:false,
                            logOutDone:true,
                            me:null,

                    }
                    case LOG_OUT_FAILURE:
                        return {
                                ...state.user,
                                logOutLoading:false,
                                logOutDone:false

                        }
                        case ADD_POST_TO_ME:
                        return {
                                ...state,
                                me:{
                                    ...state.me,
                                    Posts:[{id:action.data},...state.me.Posts]
                                },


                        }
        default:
            return state
    }
}

export default reducer
