export const initialState={
    user:{
        isLoggedIn:false,
        user:{},
        signUpData:{},
        loginData:{},
    },
    post:{
        mainPosts:[],
    }
}


export const loginAction=(data)=>{
    return{
        type:'LOG_IN',
        data
    }
}

export const logoutAction=()=>{
    return{
        type:'LOG_OUT',
    }
}

