//reducer-> (이전상태,액션)=>다음상태
const rootReducer=(state=initialState,action)=>{
    switch(action.type){
        case 'LOG_IN':
            return {
                ...state,
                user:{
                    ...state.user,
                    isLoggedIn:true,
                    user:action.data
                }

            }
            case 'LOG_OUT':
                return {
                    ...state,
                    user:{
                        ...state.user,
                        isLoggedIn:false,
                        user:null
                    }

                }
    }
};

export default rootReducer
